SELECT schools.name AS school, courses.name AS course, COUNT(educations."userId") AS "studentsCount" FROM educations JOIN schools ON educations."schoolId" = schools.id JOIN courses ON educations."courseId" = courses.id WHERE educations.status = 'ongoing' OR educations.status = 'finished' GROUP BY schools.name, courses.name ORDER BY COUNT(educations."userId") DESC LIMIT 3;